mapper.select:
select DISTINCT(p.time) date,
	case when p2.income is null then 0 else p2.income end income,
	case when p3.expend is null then 0 else p3.expend end expend
from (
	() UNION
	() 
) p 
LEFT JOIN () p2 on p.time = p2.time
LEFT JOIN () p3 on p.time = p3.time


		and p.time >= #{startDate,jdbcType=VARCHAR}


		 and p.time <= #{endDate,jdbcType=VARCHAR}  


ORDER BY p.time DESC

select sum(amount) amount,sum(penalty_amout) penaltyAmount,repay_time date from (select amount,penalty_amout, DATE_FORMAT(repay_time,'%Y-%m-%d') repay_time from cl_borrow_repay where penalty_day > 0) t 


		and repay_time >= #{startDate,jdbcType=VARCHAR}


		 and repay_time <= #{endDate,jdbcType=VARCHAR}  


GROUP BY repay_time
ORDER BY repay_time DESC

mapper.sql:
select  u.real_name realName,br.amount,DATE_FORMAT(br.repay_time,'%Y-%m-%d %T') date from cl_borrow_repay_log br LEFT JOIN cl_user_base_info u on br.user_id = u.user_id


		and DATE_FORMAT(br.repay_time,'%Y-%m-%d') >= #{startDate,jdbcType=VARCHAR}


		 and DATE_FORMAT(br.repay_time,'%Y-%m-%d') <= #{endDate,jdbcType=VARCHAR}  


ORDER BY br.repay_time DESC

select u.real_name realName,DATE_FORMAT(bp.create_time,'%Y-%m-%d %T') date,b.real_amount amount from cl_borrow_progress bp LEFT JOIN cl_borrow b on bp.borrow_id = b.id LEFT JOIN cl_user_base_info u on u.user_id = bp.user_id
bp.state = '30'
and DATE_FORMAT(bp.create_time,'%Y-%m-%d') >= #{startDate,jdbcType=VARCHAR}
and DATE_FORMAT(bp.create_time,'%Y-%m-%d') <= #{endDate,jdbcType=VARCHAR}
ORDER BY bp.create_time DESC
