mapper.update:
update arc_sys_user
set
password=#{password}
where
user_name = #{userName}

mapper.select:
select count(*) from (
select
count(1)
from arc_sys_user u,arc_sys_user_role ur,sys_role r
where u.id = ur.user_id and r.id = ur.role_id

	u.id = #{id}


	and u.name like concat('',#{name}, '%')


	and u.job_num = #{jobNumber}


	and u.company_id =#{companyId}


	and u.office_id = #{officeId}


	and u.email = #{email}


	and u.phone = #{phone}


	and u.mobile = #{mobile}


	and u.status = #{status}


	and u.position = #{position}


	and u.office_over = #{officeOver}


	and u2.role_id = #{roleId}


	and u.office_id like concat('',#{parentofficeId}, '%')

GROUP BY u.id
order by u.add_time desc
) as a

select
u.id as id,
u.office_over as officeOver,
u.email as email,
u.phone as phone,
u.mobile as mobile,
u.remark as remark,
u.position as position,
u.name as name,
u.user_name as userName,
u.job_num as
job_num,
u.office_id as officeId,
u.status as
status,
group_concat( u4.id) as roleId,
group_concat( u4.name) as
roleName
from arc_sys_user u,
arc_sys_user_role u2, arc_sys_role u4
where u.id =
u2.user_id
and u2.role_id = u4.id

	u.id = #{id}


	and u.name like concat('',#{name}, '%')


	and u.job_num = #{jobNumber}


	and u.company_id =#{companyId}


	and u.office_id = #{officeId}


	and u.email = #{email}


	and u.phone = #{phone}


	and u.mobile = #{mobile}


	and u.status = #{status}


	and u.position = #{position}


	and u.office_over = #{officeOver}


	and u2.role_id = #{roleId}


	and u.office_id like concat('',#{parentofficeId}, '%')

group by
u.id,u.office_over,u.email,u.phone,u.mobile,u.remark,u.position

select u.id as id, u.office_over as officeOver, u.email as email, u.phone as phone, u.mobile as mobile, u.remark as remark, u.position as position, u.name as name, u.user_name as userName, u.job_num as job_num, u.office_id as officeId, u.status as status, group_concat( u4.id) as roleId, group_concat( u4.name) as roleName from arc_sys_user u, arc_sys_user_role u2, arc_sys_role u4 where u.id = u2.user_id and u2.role_id = u4.id
u.id = #{id}
and u.name like concat("%",#{name}, "%")
and u.job_num like concat("%",#{jobNumber,jdbcType=VARCHAR},"%")
and u.email = #{email}
and u.phone = #{phone}
and u.mobile = #{mobile}
and u.status = #{status}
and u.position = #{position}
and u2.role_id = #{roleId}
group by u.id,u.email,u.phone,u.mobile,u.remark,u.position

SELECT usr.user_name as "userName", usr.office_id as "officeId", count(task.ASSIGNEE_) as "count" FROM arc_sys_user usr left JOIN ACT_RU_TASK task on task.ASSIGNEE_ = usr.user_name INNER JOIN arc_sys_user_role ur on ur.user_id = usr.id INNER JOIN arc_sys_role r on r.id = ur.role_id WHERE r.nid = #{roleName} AND usr.status = #{status}
AND ( usr.office_id = #{userOfficeId}
OR usr.office_id = #{userHeadOfficeId}
OR usr.office_id like concat('',#{officeId}, '%')
)
and FIND_IN_SET(#{userOverOfficeId},usr.office_over )
GROUP BY usr.user_name,usr.office_id ORDER BY count(task.ASSIGNEE_),RAND() limit 0,1

select
u.*
from arc_sys_user u
INNER JOIN arc_sys_user_role ur on u.id = ur.user_id
INNER JOIN arc_sys_role r on r.id = ur.role_id
where r.nid = #{roleName}

	and u.office_id = #{officeId}


	and u.office_id in

		#{item}

select u.id, u.name, u.user_name as userName from arc_sys_user u INNER JOIN arc_sys_user_role ur on u.id = ur.user_id INNER JOIN arc_sys_role r on r.id = ur.role_id where r.nid = #{roleName}
and u.office_id in
#{item}
